            userData: {},
            followers: [],
            following: [],
            repos: []
        }
    componentDidMount(){

        //Setting UserData
        Axios.get('https://api.github.com/users/VanshikaP')
        .then(response => {
            this.setState({
                ...this.state, userData: response.data
            })
        })

        // Setting Followers
        Axios.get(this.state.userData.followers_url)
        .then(response => {
            this.setState({
