import React, {useEffect} from 'react'
import Axios from 'axios'

function UserCard(props) {
    const followers = 0;
    const following = 0;

    // Setting Followers
    useEffect(() => {
        Axios.get('https://api.github.com/users/VanshikaP/followers')
        .then(response => {
            this.setState({
                ...this.setState, followers: response.data.length
            })
            console.log('No. of Followers', response.data.length)
        })
        .catch(err => console.log('Error', err.message));
    }, [])

    return (
        <div className='user-container'>
            <h2> {props.userData.name} </h2>
            <div className = 'basic'>
                <p>GitHub Username: {props.userData.login} </p>
                <p>Profile: {props.userData.html_url} </p>
                <div className='followers'>
                    <p> {followers} followers</p>
                </div>
            </div>
        </div>
    )
}

export default UserCard


